const Courses = (props) => {
  for (const course of props.courses) {
    const Course = (props) => {
      /*
      const headers = props.courses.map(name => name.name)
      console.log('headers', headers)

      const Header = (props) => {
        const header = props.headers.map()
        console.log('header', header)
        return (
          <div>
          <h1>
            {header}
          </h1>
          </div>
        )
      }

            <Header headers={headers} />
      */

      const Content = (props) => (
        <div>
          {props.course.parts.map(part => 
            <p key={part.id} >
              {part.name} {part.exercises}
            </p>
          )}
        </div>
      )

      const Total = (parts) => {
        const sum = parts.parts.reduce(
          (accumulator, currentValue) => accumulator + currentValue.exercises,
          0,
        )
        return (
          <b>total of {sum} exercises</b>
        )
      }

      return (
        <div>
          <Content course={course} />
          {console.log('props.courses', props.course.parts)}
          {console.log('COURSE', course.parts)}
          <Total parts={course.parts} />
        </div>
      )
    }

    return <Course course={course} />
  }


  }





const App = () => {
  const courses = [
    {
      name: 'Half Stack application development',
      id: 1,
      parts: [
        {
          name: 'Fundamentals of React',
          exercises: 10,
          id: 1
        },
        {
          name: 'Using props to pass data',
          exercises: 7,
          id: 2
        },
        {
          name: 'State of a component',
          exercises: 14,
          id: 3
        },
        {
          name: 'Redux',
          exercises: 11,
          id: 4
        }
      ]
    }, 
    {
      name: 'Node.js',
      id: 2,
      parts: [
        {
          name: 'Routing',
          exercises: 3,
          id: 1
        },
        {
          name: 'Middlewares',
          exercises: 7,
          id: 2
        }
      ]
    }
  ]

  return <Courses courses={courses} />
} 

export default App
